import React, { Component } from 'react';
import axios from 'axios'



class Main extends Component {



    constructor(props) {
        super(props)

        this.state= {
            allCourses: []

        }
    }

    componentDidMount(){
        axios.get('https://saral.navgurukul.org/api/courses')
        .then(posRes=>{
            const allCourses = posRes.data.availableCourses
            // console.log(allCourses,"poooooooo")
            this.setState({allCourses:allCourses});
        })
        .catch(errRes=>{
            console.log('errRes')

        })
    }


    render() {
        console.log(this.state.allCourses,"dsahkjfasdkjfkjash")

        return (
            <div className="container">
                {this.state.allCourses.map(e => 
                    <div className="card mb-3" style="max-width: 540px;">
  <div className="row no-gutters">
    <div className="col-md-4">
      <img src={e.logo} class="card-img" alt="..."></img>
    </div>
    <div class="col-md-8">
      <div class="card-body">
        <h5 class="card-title">{e.name} </h5>
        <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
        <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
      </div>
    </div>
  </div>
</div>)}

            </div>
        )
    }
}
 export default Main;
