import React, { Component } from 'react';
import axios from 'axios'



class Main extends Component {


    constructor(props) {
        super(props)

        this.state= {
            allCourses: []

        }
    }

    componentDidMount(){
        axios.get('https://saral.navgurukul.org/api/courses')
        .then(posRes => {
            const allCourses = posRes.data.availableCourses
            // console.log(allCourses,"poooooooo")
            this.setState({allCourses:allCourses});
        })
        .catch(errRes=>{
            console.log('errRes')

        })
    }


    render() {
        console.log(this.state.allCourses,"dsahkjfasdkjfkjash")

        return (
            <div className="container">
                {this.state.allCourses.map(element => 
                    <div class="card mb-3" style="max-width: 540px;">
                        <div class="row no-gutters">
                            <div class="col-md-4">
                                <img src={element.logo} class="card-img" alt="..."></img>
                            </div>
                            <div class="col-md-8">
                                <div class="card-body">
                                    <h5 class="card-title">{element.name}</h5>
                                </div>
                            </div>
                        </div>
                    </div>)}

            </div>
        )
    }
}
 export default Main;
